  KEY `type` (`type`) USING BTREE,
  KEY `type_2` (`type`) USING BTREE,
  FULLTEXT KEY `content` (`content`)
) ENGINE=MyISAM AUTO_INCREMENT=87 DEFAULT CHARSET=utf8;

