insert  into `my_channels`(`id`,`pid`,`cid`,`channeltype`,`systemtype`,`ordernum`,`ishidden`,`positions`,`alias`,`title`,`content`,`seotitle`,`metakeywords`,`metadesc`,`link`,`target`,`create_time`,`update_time`) values (1,0,0,2,0,0,0,'1','','基本信息','基本信息','','','','',0,1413687809,1413687809),(2,0,0,2,0,0,0,'1','','招生考试','招生信息','','','','',0,1413687847,1414636359),(3,0,0,2,0,0,0,'1','','财务资产','财务信息','','','','',0,1413687900,1414636369),(4,0,0,2,0,0,0,'1','','人事师资','人事师资','','','','',0,1413687938,1413687938),(5,0,0,2,0,0,0,'1','','教学质量','教学质量','','','','',0,1413687968,1413687968),(6,0,0,2,0,0,0,'1','','学生管理','学生管理服务','','','','',0,1413687997,1414552354),(7,0,0,2,0,0,0,'1','','学风建设','','','','','',0,1413688181,1413688181),(8,0,0,2,0,0,0,'1','','学位学科','','','','','',0,1413688205,1413688205),(9,0,0,2,0,0,0,'1','','对外交流','','','','','',0,1413688227,1414636342),(10,0,0,2,0,0,0,'1','','其他','','','','','',0,1413688240,1413688240),(11,1,0,2,0,0,0,'1','','办学规模、校级领导班子简介及分工、学校机构设置、学科情况、专业情况、各类在校生情况、教师和专业技术人员数量等办学基本情况','<p style=\"text-indent:2em;\">\r\n	<span> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">中国矿业大学（北京）是教育部直属的全国重点高校、国家“</span><span style=\"font-size:16px;font-family:SimSun;\">211</span><span style=\"font-size:16px;font-family:SimSun;\">工程”和“</span><span style=\"font-size:16px;font-family:SimSun;\">985</span><span style=\"font-size:16px;font-family:SimSun;\">优势学科创新平台项目”建设高校，是全国首批产业技术创新战略联盟高校，同时也是教育部与国家安全生产监督管理总局共建高校。</span><span style=\"font-size:16px;font-family:SimSun;\">1960</span><span style=\"font-size:16px;font-family:SimSun;\">年和</span><span style=\"font-size:16px;font-family:SimSun;\">1978</span><span style=\"font-size:16px;font-family:SimSun;\">年，学校先后两次被确定为全国重点高校，为全国首批具有博士和硕士授予权的高校之一，设有研究生院。学校有两个校区：学院路校区坐落于北京市高校云集的海淀区学院路，沙河校区坐落于北京市昌平沙河高教区。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">学校的前身是由英国福公司创办于</span><span style=\"font-size:16px;font-family:SimSun;\">1909</span><span style=\"font-size:16px;font-family:SimSun;\">年的焦作路矿学堂。</span><span style=\"font-size:16px;font-family:SimSun;\">1950</span><span style=\"font-size:16px;font-family:SimSun;\">年学校由焦作迁至天津，更名为中国矿业学院。</span><span style=\"font-size:16px;font-family:SimSun;\">1952</span><span style=\"font-size:16px;font-family:SimSun;\">年院系调整期间，天津大学、唐山交大、清华大学的采矿系调整到中国矿业学院，学校因此聚集了全国一流的采矿科学技术人才。</span><span style=\"font-size:16px;font-family:SimSun;\">1953</span><span style=\"font-size:16px;font-family:SimSun;\">年，为实现更大的发展，学校迁至北京，改称北京矿业学院，成为北京学院路著名的“八大学院”之一。“文革”期间，学校迁至四川省合川县位于华蓥山下的三汇坝镇，更名为四川矿业学院。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">1978年，学校在江苏徐州重建，恢复中国矿业学院校名并在北京学院路原址设立北京研究生部。</span><span style=\"font-size:16px;font-family:SimSun;\">1988</span><span style=\"font-size:16px;font-family:SimSun;\">年，学校更名为中国矿业大学，邓小平同志亲笔为学校题写校名。</span><span style=\"font-size:16px;font-family:SimSun;\">1997</span><span style=\"font-size:16px;font-family:SimSun;\">年，经国家教委批准设立中国矿业大学（北京）。</span><span style=\"font-size:16px;font-family:SimSun;\">1998</span><span style=\"font-size:16px;font-family:SimSun;\">年，恢复本科生教育。</span><span style=\"font-size:16px;font-family:SimSun;\">2000</span><span style=\"font-size:16px;font-family:SimSun;\">年，学校整体划转教育部直属管理。</span><span style=\"font-size:16px;font-family:SimSun;\">2003</span><span style=\"font-size:16px;font-family:SimSun;\">年</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">月，经中央编制部门批复同意，以“中国矿业大学（北京）”名义办学。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">学校建设与发展，得到了党和国家领导人亲切关怀。毛泽东同志“开发矿业”的题词曾激励着一代代矿大人为事业不懈奋斗；邓小平同志亲笔批示指引学校在改革开放中走上中兴之路；江泽民同志来校视察学校科研工作并为我校建校</span><span style=\"font-size:16px;font-family:SimSun;\">90</span><span style=\"font-size:16px;font-family:SimSun;\">周年题词；胡锦涛同志为我校建校</span><span style=\"font-size:16px;font-family:SimSun;\">100</span><span style=\"font-size:16px;font-family:SimSun;\">周年发来贺信。</span><span style=\"font-size:16px;font-family:SimSun;\">100</span><span style=\"font-size:16px;font-family:SimSun;\">多年来，学校与中国工业化进程同步发展，与人民共和国一起成长。经过一代又一代矿大人的努力奋斗，学校铸就了中国煤炭高等教育的一流品牌和独特的精神文化品格，形成了“勤奋、求实、进取、奉献”的校风、“好学力行”的学风，积淀形成了艰苦奋斗的创业精神和迎难而上的拼搏精神。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">中国矿业大学（北京）在上级主管部门、煤炭能源行业和社会各界的支持下，通过长期发展和建设，已经形成了以工科为主、以矿业与安全为特色，理工文管法经等多学科协调发展的学科专业体系和多科性大学的基本格局。在煤炭能源的勘探、开发、利用，资源、环境和生产相关的矿建、安全、测绘、机械、信息技术、生态恢复、管理工程等领域形成了优势品牌和鲜明特色。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">学校现有</span><span style=\"font-size:16px;font-family:SimSun;\">62</span><span style=\"font-size:16px;font-family:SimSun;\">个本科专业，有</span><span style=\"font-size:16px;font-family:SimSun;\">16</span><span style=\"font-size:16px;font-family:SimSun;\">个一级学科博士点，</span><span style=\"font-size:16px;font-family:SimSun;\">35</span><span style=\"font-size:16px;font-family:SimSun;\">个一级学科硕士点，</span><span style=\"font-size:16px;font-family:SimSun;\">69</span><span style=\"font-size:16px;font-family:SimSun;\">个二级学科博士点，</span><span style=\"font-size:16px;font-family:SimSun;\">173</span><span style=\"font-size:16px;font-family:SimSun;\">个二级学科硕士点，</span><span style=\"font-size:16px;font-family:SimSun;\">10</span><span style=\"font-size:16px;font-family:SimSun;\">个硕士专业学位授权点；</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">个一级学科国家重点学科，</span><span style=\"font-size:16px;font-family:SimSun;\">8</span><span style=\"font-size:16px;font-family:SimSun;\">个二级学科国家重点学科，</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">个国家重点培育学科，</span><span style=\"font-size:16px;font-family:SimSun;\">21</span><span style=\"font-size:16px;font-family:SimSun;\">个省部级重点学科；</span><span style=\"font-size:16px;font-family:SimSun;\">14</span><span style=\"font-size:16px;font-family:SimSun;\">个博士后科研流动站。在教育部</span><span style=\"font-size:16px;font-family:SimSun;\">2012</span><span style=\"font-size:16px;font-family:SimSun;\">年第三轮学科评估中，矿业工程、安全科学与工程、测绘科学与技术、地质资源与地质工程分别排名第一、一、三、四位。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">学校拥有一支高水平的师资队伍，现有各类教职工</span><span style=\"font-size:16px;font-family:SimSun;\">911</span><span style=\"font-size:16px;font-family:SimSun;\">人。专任教师中，有教授</span><span style=\"font-size:16px;font-family:SimSun;\">147</span><span style=\"font-size:16px;font-family:SimSun;\">人，副教授</span><span style=\"font-size:16px;font-family:SimSun;\">167</span><span style=\"font-size:16px;font-family:SimSun;\">人；博士生导师</span><span style=\"font-size:16px;font-family:SimSun;\">190</span><span style=\"font-size:16px;font-family:SimSun;\">名，硕士生导师</span><span style=\"font-size:16px;font-family:SimSun;\">175</span><span style=\"font-size:16px;font-family:SimSun;\">名，拥有博士学位的教师比例达</span><span style=\"font-size:16px;font-family:SimSun;\">74%</span><span style=\"font-size:16px;font-family:SimSun;\">以上。教师队伍中，有教育部“创新团队发展计划”</span><span style=\"font-size:16px;font-family:SimSun;\">4</span><span style=\"font-size:16px;font-family:SimSun;\">个，拥有</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">名中国科学院院士、</span><span style=\"font-size:16px;font-family:SimSun;\">6</span><span style=\"font-size:16px;font-family:SimSun;\">名中国工程院院士，“长江学者奖励计划”特聘教授</span><span style=\"font-size:16px;font-family:SimSun;\">7</span><span style=\"font-size:16px;font-family:SimSun;\">人；先后有</span><span style=\"font-size:16px;font-family:SimSun;\">7</span><span style=\"font-size:16px;font-family:SimSun;\">人获国家杰出青年科学基金，</span><span style=\"font-size:16px;font-family:SimSun;\">10</span><span style=\"font-size:16px;font-family:SimSun;\">人入选“新世纪百千万人才工程”国家级人选，</span><span style=\"font-size:16px;font-family:SimSun;\">44</span><span style=\"font-size:16px;font-family:SimSun;\">人被列入教育部跨世纪、新世纪优秀人才支持计划，</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">人获首届中国青年科学家奖，</span><span style=\"font-size:16px;font-family:SimSun;\">4</span><span style=\"font-size:16px;font-family:SimSun;\">人被评为国家有突出贡献的中青年专家，</span><span style=\"font-size:16px;font-family:SimSun;\">2</span><span style=\"font-size:16px;font-family:SimSun;\">人被评为“全国优秀教师”，</span><span style=\"font-size:16px;font-family:SimSun;\">4</span><span style=\"font-size:16px;font-family:SimSun;\">人获全国五一劳动奖章，</span><span style=\"font-size:16px;font-family:SimSun;\">4</span><span style=\"font-size:16px;font-family:SimSun;\">人分别获孙越崎能源大奖，</span><span style=\"font-size:16px;font-family:SimSun;\">5</span><span style=\"font-size:16px;font-family:SimSun;\">人获中国青年科技奖，</span><span style=\"font-size:16px;font-family:SimSun;\">3</span><span style=\"font-size:16px;font-family:SimSun;\">人被评为“全国优秀博士后”。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">学校始终坚持以育人为本，积极构建能源工业精英教育教学体系，致力于培养德智体全面发展、富有社会责任感、创新精神和实践能力的高素质人才。广大毕业生当中许多人已成长为国家现代化建设及行业发展的科技精英、管理骨干和领军人物。学校先后有</span><span style=\"font-size:16px;font-family:SimSun;\">8</span><span style=\"font-size:16px;font-family:SimSun;\">个专业荣获国家“第一类特色专业建设点”项目，</span><span style=\"font-size:16px;font-family:SimSun;\">8</span><span style=\"font-size:16px;font-family:SimSun;\">个专业荣获北京市高等学校特色专业项目，“安全工程”专业在全国第一个通过教育部工程教育专业认证。拥有</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">个国家级实验教学示范中心，</span><span style=\"font-size:16px;font-family:SimSun;\">2</span><span style=\"font-size:16px;font-family:SimSun;\">个国家级工程实践教育中心；</span><span style=\"font-size:16px;font-family:SimSun;\">1998</span><span style=\"font-size:16px;font-family:SimSun;\">年以来，我校学生参加省级以上国内外科技竞赛千余人次获奖；先后有</span><span style=\"font-size:16px;font-family:SimSun;\">9</span><span style=\"font-size:16px;font-family:SimSun;\">篇博士论文入选全国百篇优秀博士论文。目前全校有全日制普通本科生</span><span style=\"font-size:16px;font-family:SimSun;\">5900</span><span style=\"font-size:16px;font-family:SimSun;\">余人，各类硕士、博士研究生</span><span style=\"font-size:16px;font-family:SimSun;\">6100</span><span style=\"font-size:16px;font-family:SimSun;\">余人，成人教育本专科生3100余人，留学生</span><span style=\"font-size:16px;font-family:SimSun;\">34</span><span style=\"font-size:16px;font-family:SimSun;\">人。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">围绕人才培养的核心任务和国家及行业的战略需求，学校致力于科学研究，提升学校的创新能力，取得了一大批高水平科技成果。“十五”以来，先后获得国家级科技奖励</span><span style=\"font-size:16px;font-family:SimSun;\">22</span><span style=\"font-size:16px;font-family:SimSun;\">项，省部级科技奖励</span><span style=\"font-size:16px;font-family:SimSun;\">232</span><span style=\"font-size:16px;font-family:SimSun;\">项。在研科研项目</span><span style=\"font-size:16px;font-family:SimSun;\">770</span><span style=\"font-size:16px;font-family:SimSun;\">余项，研究与发展经费快速增长。学校建设了完备的高水平科技创新平台，拥有</span><span style=\"font-size:16px;font-family:SimSun;\">2</span><span style=\"font-size:16px;font-family:SimSun;\">个国家重点实验室，</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">个国家工程研究中心，</span><span style=\"font-size:16px;font-family:SimSun;\">2</span><span style=\"font-size:16px;font-family:SimSun;\">个教育部工程研究中心、</span><span style=\"font-size:16px;font-family:SimSun;\">1</span><span style=\"font-size:16px;font-family:SimSun;\">个北京市重点实验室。学校图书馆拥有藏书</span><span style=\"font-size:16px;font-family:SimSun;\">79</span><span style=\"font-size:16px;font-family:SimSun;\">万余册，电子图书</span><span style=\"font-size:16px;font-family:SimSun;\">55</span><span style=\"font-size:16px;font-family:SimSun;\">万册。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">学校积极发挥自身优势，服务行业人才培养、科技进步及地方经济社会发展，推进产学研协同创新。以校理事会为平台大力推进社会服务，校理事会成员单位发展到</span><span style=\"font-size:16px;font-family:SimSun;\">77</span><span style=\"font-size:16px;font-family:SimSun;\">家；建成了我国首家以能源与安全为特色的科技园</span><span style=\"font-size:16px;font-family:SimSun;\">---</span><span style=\"font-size:16px;font-family:SimSun;\">“中关村能源与安全科技园”和“中国矿业大学留学人员创业园”，并与北京市共建能源安全产业技术研究院。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">学校积极开展国内外学术交流与合作，不断推进国际化办学。目前，与国内</span><span style=\"font-size:16px;font-family:SimSun;\">10</span><span style=\"font-size:16px;font-family:SimSun;\">所高水平行业特色型大学联合发起成立“北京高科大学联盟”，与</span><span style=\"font-size:16px;font-family:SimSun;\">21</span><span style=\"font-size:16px;font-family:SimSun;\">所世界著名高校和科研院所签订了校际合作协议，与</span><span style=\"font-size:16px;font-family:SimSun;\">80</span><span style=\"font-size:16px;font-family:SimSun;\">余所国外知名大学和科研院所的相关学科开展了学术交流；多次举办了国际煤岩学委员会年会、国际有机岩石学年会（</span><span style=\"font-size:16px;font-family:SimSun;\">ICCP-TSOP</span><span style=\"font-size:16px;font-family:SimSun;\">）联合会议、第一届行为安全与安全管理国际会议、国际有机岩石学会（</span><span style=\"font-size:16px;font-family:SimSun;\">TSOP</span><span style=\"font-size:16px;font-family:SimSun;\">）年会、国际采矿科学与技术大会等国际性学术会议，学术交流日益活跃。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\">中国矿业大学（北京）坚持“面向现代化、面向世界、面向未来”的指导方针，按照“强化特色、提高质量、充实内涵、外向发展”的总体发展思路，抓住“统筹规划、队伍建设、科学管理”三个关键，使学校整体办学水平实现新的跨越发展，努力把学校建成特色鲜明、国际知名的高水平矿业大学。（</span><span style=\"font-size:16px;font-family:SimSun;\">2014</span><span style=\"font-size:16px;font-family:SimSun;\">年</span><span style=\"font-size:16px;font-family:SimSun;\">7</span><span style=\"font-size:16px;font-family:SimSun;\">月</span><span style=\"font-size:16px;font-family:SimSun;\">12</span><span style=\"font-size:16px;font-family:SimSun;\">日更新）</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;font-family:SimSun;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:16px;\"><strong>校级领导班子成员：</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\"><strong> </strong></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;书 &nbsp; &nbsp;记： </span><a href=\"http://www.cumtb.edu.cn/frameset/xrld/xxm.htm\"><span style=\"font-size:14px;\">徐孝民</span></a><span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-size:14px;font-family:SimSun;\">校　长：</span><span><span style=\"font-size:10px;font-family:SimSun;\"><a href=\"http://www.cumtb.edu.cn/frameset/xrld/yrs.htm\"><span style=\"font-size:14px;\">杨仁树</span></a></span></span> </strong> \r\n</p>\r\n<p>\r\n	<strong><span><span style=\"font-size:14px;font-family:SimSun;\">&nbsp; &nbsp; &nbsp;&nbsp;副书记：</span><span><span style=\"font-size:14px;\">&nbsp;</span><a href=\"http://www.cumtb.edu.cn/frameset/xrld/zsq.htm\"><span style=\"font-size:14px;font-family:SimSun;\">朱书全</span></a></span><span style=\"font-size:14px;\"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-size:14px;font-family:SimSun;\">副校长：</span><span><a href=\"http://www.cumtb.edu.cn/frameset/xrld/sjp.htm\"><span style=\"font-size:14px;font-family:SimSun;\">孙继平</span></a></span><span style=\"font-size:14px;\"></span><br />\r\n</span> </strong> \r\n</p>\r\n<p>\r\n	<strong><span><span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;</span><span style=\"font-size:14px;font-family:SimSun;\">副校长：</span><span><a href=\"http://www.cumtb.edu.cn/frameset/xrld/fx.htm\"><span style=\"font-size:14px;font-family:SimSun;\">范 &nbsp;迅</span></a></span><span style=\"font-size:14px;\"></span></span> </strong> \r\n</p>\r\n<p>\r\n	<strong><span><span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-size:14px;font-family:SimSun;\">副校长：</span><span><a href=\"http://www.cumtb.edu.cn/frameset/xrld/jyd.htm\"><span style=\"font-size:14px;font-family:SimSun;\">姜耀东</span></a></span><span style=\"font-size:14px;\"></span></span> </strong> \r\n</p>\r\n<p>\r\n	<strong><span><span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-size:14px;font-family:SimSun;\">副校长：</span><span><a href=\"http://www.cumtb.edu.cn/frameset/xrld/wzq.htm\"><span style=\"font-size:14px;font-family:SimSun;\">王忠强</span></a></span><span style=\"font-size:14px;\"></span></span> </strong> \r\n</p>\r\n<strong><br />\r\n</strong> \r\n<p>\r\n	<br />\r\n</p>\r\n<br />\r\n<br />\r\n<p>\r\n	<br />\r\n</p>','办学规模、校级领导班子成员','','','',0,1413694073,1414809159),(12,1,0,2,0,0,0,'1','','学校章程及制定的各项规章制度','<span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">中国矿业大学（北京）是教育部直属的全国重点高校、国家“</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">211</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">工程”和“</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">985</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">优势学科创新平台项目”建设高校，是全国首批产业技术创新战略联盟高校，同时也是教育部与国家安全生产监督管理总局共建高校。</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">1960</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">年和</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">1978</span><span style=\"font-size:16px;font-family:SimSun;line-height:20px;background-color:#FFFFFF;\">年，学校先后两次被确定为全国重点高校，为全国首批具有博士和硕士授予权的高校之一，设有研究生院。学校有两个校区：学院路校区坐落于北京市高校云集的海淀区学院路，沙河校区坐落于北京市昌平沙河高教区。</span>','','','','',0,1413694102,1414654845),(13,1,0,2,0,0,0,'1','','教职工代表大会相关制度、工作报告','','','','','',0,1413904538,1414809216),(14,1,0,2,0,0,0,'1','','学术委员会相关制度、年度报告','','','','','',0,1413904708,1414654902),(15,1,0,2,0,0,0,'1','','学校发展规划、年度工作计划及重点工作安排','','','','','',0,1413904781,1414809243),(16,1,0,2,0,0,0,'1','','信息公开年度报告','<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:14px;\">中国矿业大学（北京）按照《高等学校信息公开办法》（教育部令第29号）和《教育部关于公布&lt;高等学校信息公开事项清单&gt;的通知》（教办函[2014]23号）的有关要求，牢固树立“以公开为原则，不公开为例外”的理念，为更好地服务师生和社会公众，积极推进并扎实做好信息公开工作。2013-2014学年信息公开年度报告，由信息公开概述、信息公开情况、主要问题和改进措施三部分组成。本报告中所列数据的统计期限自2013年9月1日起至2014年8月31日止。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.15pt;\">\r\n	<b><span style=\"font-size:14px;\">一、信息公开概述</span><span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">（一）完善信息公开制度</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">2013-2014学年，中国矿业大学（北京）积极贯彻落实《办法》，构建了学校党政主要负责人领导、党政办公室牵头负责协调、各单位具体实施、纪委监察部门监督检查、师生员工积极参与的信息公开工作机制；学校专门成立了信息公开领导小组、信息公开办公室，明确了各组织机构的工作职责；学校出台了《中国矿业大学（北京）信息公开实施办法（试行）》（中矿大京字</span><span style=\"font-size:14px;\">[2011]11</span><span style=\"font-size:14px;\">号）、《中国矿业大学（北京）信息公开保密审查暂行规定》（中矿大京字</span><span style=\"font-size:14px;\">[2011]13</span><span style=\"font-size:14px;\">号）、《中国矿业大学（北京）&lt;高等学校信息公开事项清单&gt;落实细化方案》（中矿大京字</span><span style=\"font-size:14px;\">[2014]30</span><span style=\"font-size:14px;\">号），进一步修订和完善了《信息公开指南》、《信息公开目录》。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">（二）改版升级信息公开网站</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">按照教育部有关要求，规范学校信息公开工作，便于师生及社会公众对学校属于应当公开的信息进行查询，推进信息公开网上专栏的规范化、标准化建设，2014年，学校对信息公开网进行了改版升级，将原先信息公开栏目增加到10个一级类目50个二级目录，增加了公开信息内容检索，实现了与学校现有网站资源的关联和共享，保证了信息的唯一性、及时性、准确性。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">&nbsp;</span><b><span style=\"font-size:14px;\">二、信息公开情况</span></b><b></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（一）主动公开信息情况</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">1．主动公开信息数量</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">2013-2014学年，全校新增的主动公开信息共</span><span style=\"font-size:14px;\">2165</span><span style=\"font-size:14px;\">条，其中通过校园网公开的信息达到</span><span style=\"font-size:14px;\">1200</span><span style=\"font-size:14px;\">余条。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">2．主动公开信息内容</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">1</span><span style=\"font-size:14px;\">）学校名称、办学地点、办学性质、办学宗旨、办学层次、办学规模、机构设置等基本信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">2</span><span style=\"font-size:14px;\">）学校教育教学、科学研究、服务社会和文化传承创新等方面的规划，总结等信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">3</span><span style=\"font-size:14px;\">）校园文化、理论宣贯、政策督查，反腐倡廉等党务信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">4</span><span style=\"font-size:14px;\">）学校各学历层次和各类学生招生录取、学生学籍管理、教学成果申报及评审结果、课程设置、实习答辩、学位授予、推免研究生、本科生转专业等教学管理信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">5</span><span style=\"font-size:14px;\">）学科与专业设置，重点学科建设等学科建设信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">6</span><span style=\"font-size:14px;\">）学校科研项目申报、科研奖励制度和科研项目结果等科研管理信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">7</span><span style=\"font-size:14px;\">）干部培训与考核、人事任免信息、招考录用、职称评审办法和结果等有关人事工作信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:22.35pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">8</span><span style=\"font-size:14px;\">）学校财务、资产相关规章制度，年度预决算方案，</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">学校经费来源构成、资产、图书馆藏书，教育收费管理项目、依据、标准与投诉方式等财务资产信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">&nbsp;&nbsp; （</span><span style=\"font-size:14px;\">9</span><span style=\"font-size:14px;\">）饮食服务、校园安保、校园规划与建设等后勤保卫基建信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:23.95pt;\">\r\n	<span style=\"font-size:14px;\">（</span><span style=\"font-size:14px;\">10</span><span style=\"font-size:14px;\">）帮困助学、学生奖励、就业指导等学生管理信息。</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:22.35pt;text-indent:8.0pt;\">\r\n	<span style=\"font-size:14px;\">3．主动公开信息形式</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">通过校园网、会议、校发文件、校报、宣传栏、橱窗、海报、广播、电视台等形式及时公开学校信息。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">（二）依申请公开情况</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">中国矿业大学（北京）已在《信息公开实施办法（试行）》和《信息公开指南》中明确了依申请公开有关要求。在</span><span style=\"font-size:14px;\">2013-2014</span><span style=\"font-size:14px;\">学年中没有收到相关依申请公开信息。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">（三）对信息公开的评议情况</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">学校不定期组织师生代表以座谈会等形式对全校信息公开工作情况进行评议，评议内容包括信息公开工作的制度建设、公开形式、公开效率、服务质量等方面。学校师生对学校信息公开工作给予了普遍认可和高度评价。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">（四）信息公开工作举报情况</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.0pt;\">\r\n	<span style=\"font-size:14px;\">2013-2014学年度，学校没有收到信息公开工作的任何举报。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:32.15pt;\">\r\n	<b><span style=\"font-size:14px;\">三、主要问题和改进措施</span><span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:30pt;\">\r\n	<span style=\"font-size:14px;\">学校信息公开工作自开展以来取得了一定的成效，但在工作开展中仍存在一些问题。如信息公开意识需进一步加强，信息公开培训和业务指导力度仍需加强；一些基层单位部门的信息公开工作实施情况还不够平衡、不够规范；在主动公开、依申请公开等深化信息公开的长效机制建设方面还有待进一步探索和完善。下一阶段拟重点做好以下工作：</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:30pt;\">\r\n	<span style=\"font-size:14px;\">（一）进一步完善信息公开工作机制。不断强化校院两级领导工作机构，建立起科学高效的信息公开工作机制。进一步明确信息公开的基本原则，切实规范信息公开的范围、方式和程序，加强信息公开的监督考核和保障措施，确保信息公开工作制度化、规范化、专业化和常态化。</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:30pt;\">\r\n	<span style=\"font-size:14px;\">（二）进一步加强对学校信息公开工作的宣传和培训。学校从事信息公开工作的专职人员较少，工作任务较为繁重，又加之信息公开工作的复杂性，需要进一步加强全校各部门对信息公开有关法律法规和规章制度等方面知识的培训力度，进一步增强各部门对信息公开工作的重要性认识，促进学校信息公开工作的规范和完善，不断提升学校信息公开整体工作水平。</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:30pt;\">\r\n	<span style=\"font-size:14px;\">（三）进一步畅通办事公开的意见反馈渠道，积极探索学校与公众的双向交流。在加强网络技术的运用，逐步实现公众网上评议、网上咨询和网上征求意见的同时，进一步开设申请信息公开受理专线，统一公布各相关部门的监督电话，方便群众监督和咨询。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">&nbsp;</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">&nbsp;</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0pt;\">\r\n	<span style=\"font-size:14px;\">&nbsp;</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"right\" style=\"text-align:right;\">\r\n	<span style=\"font-size:14px;\">中国矿业大学（北京）</span><span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"right\" style=\"text-align:right;\">\r\n	<span style=\"font-size:14px;\">2014年</span><span style=\"font-size:14px;\">10</span><span style=\"font-size:14px;\">月</span><span style=\"font-size:14px;\">25</span><span style=\"font-size:14px;\">日</span>\r\n</p>\r\n<span style=\"font-size:14px;\"></span><br />','中国矿业大学（北京） 2013-2014学年信息公开年度报告','','','',0,1413904809,1414570949),(18,2,0,2,0,0,0,'1','','保送、自主选拔录取、高水平运动员和艺术特长生招生等特殊类型招生入选考生资格及测试结果','','','','','',0,1413904902,1414809433),(19,2,0,2,0,0,0,'1','','考生个人录取信息查询渠道和办法，分批次、分科类录取人数和录取最低分','','','','','',0,1413905408,1414809471),(20,2,0,2,0,0,0,'1','','招生咨询及考生申诉渠道，新生复查期间有关举报、调查及处理结果','','','','','',0,1413905453,1414809500),(21,2,0,2,0,0,0,'1','','研究生招生简章、招生专业目录、复试录取办法，各院（系、所）或学科、专业招收研究生人数','','','','','',0,1413905